const menu=document.querySelector("#menu-toggle-checkbox");menu.addEventListener("change",function(){updateBodyScrollLock()}),window.addEventListener("resize",function(){updateBodyScrollLock()},{passive:!0});function updateBodyScrollLock(){!mdScreen()&&menu.checked?document.body.classList.add("overflow-hidden"):document.body.classList.remove("overflow-hidden")}function mdScreen(){return window.matchMedia("(min-width: 768px)").matches}
